import React, { Component } from 'react'
import Modal from 'react-awesome-modal'
import PinInput from 'react-code-input'

export default class PinPopup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            pin: -1
        }
    }

    confirmPin() {
        this.setState({
            visible: true,
            pin: document.getElementById("pin-input").value
        });
    }

    OpenModal() {
        this.setState({
            visible: true
        });
    }

    closeModal() {
        this.setState({
            visible: false
        });
    }
    render() {
        return (
            <div className="table-pin-popup">
                <Modal visible={this.state.visible} width="800" height="400" effect="fadeInDown" onClickAway={() => this.closeModal()}>
                    <div className="popup-content">
                        <PinInput id="pin-input" type="tel" fields={4}></PinInput>
                        <button onClick={() => this.confirmPin()}>Confirm</button>
                        <button onClick={() => this.closeModal()}>Cancel</button>
                    </div>
                </Modal>
            </div>
        )
    }
}
