import React, { useState, useEffect } from 'react'
import PinPopup from '../components/PinPopup'
import useModal from 'react-hooks-use-modal'
import PinInput from 'react-code-input'

export default function Table() {

    const [posts, addPosts] = useState([])

    const [Modal, open, close, isOpen] = useModal('root', {
        preventScroll: true
    })

    useEffect(() => {
        pullPosts()
    }, [])

    const pullPosts = async () => {
        try {
            const res = await fetch("http://localhost:5000/all")
            const tableData = await res.json()
            addPosts(tableData)
        } catch (error) {
            console.log(error.message)
        }
    }

    return (
        <div className="table-content">
            <table className="table">
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Name</th>
                        <th>Message</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {posts.map(post => (
                        <tr key={post.post_id}>
                            <td>{post.post_creation}</td>
                            <td>{post.post_name}</td>
                            <td><button className="btn" onClick={() => PinPopup.openModal()}>Reveal</button></td>
                            <td><button className="btn">Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <Modal visible={this.state.visible} width="800" height="400" effect="fadeInDown" onClickAway={() => this.closeModal()}>
                <div className="popup-content">
                    <PinInput id="pin-input" type="tel" fields={4}></PinInput>
                    <button onClick={() => this.confirmPin()}>Confirm</button>
                    <button onClick={() => this.closeModal()}>Cancel</button>
                </div>
            </Modal>
        </div>
    )
}
