import React from 'react';

const RevealMessage = ({post, save, }) => {
    const [messageVisible, setMessageVisible] = useState(false)
    const showMessage = () => {
        setMessageVisible(true)
    }
    return (
        <div>
            {messageVisible ? post.post_message : <button className="btn" onClick={() => save(post.post_id)}>Reveal</button>}
        </div>
    );
};

export default RevealMessage;
