import React, {useState} from 'react';

const RevealMessage = ({post, save}) => {

    return (
        <div id={`id${post.post_id}`}>
            {post.post_message !== "" ? post.post_message : <button className="btn" onClick={() => save(post.post_id)}>Reveal</button>}
        </div>
    );
};

export default RevealMessage;
