import React, {useState} from 'react';

const RevealMessage = ({post, save}) => {
    const [messageVisible, setMessageVisible] = useState(false)

    return (
        <div>
            {messageVisible ? <p>post.post_message</p> : <button className="btn" onClick={() => save(post.post_id)}>Reveal</button>}
        </div>
    );
};

export default RevealMessage;
